        <span class="token keyword">const</span> canvas <span class="token operator">=</span> document<span class="token punctuation">.</span><span class="token function">getElementById</span><span class="token punctuation">(</span><span class="token string">"renderCanvas"</span><span class="token punctuation">)</span><span class="token punctuation">;</span> <span class="token comment">// Get the canvas element</span>
